on_submit.value:
async () => {
  const userdb = await db.m_user.findFirst({
    where: {
      username: {
        equals: form.username,
        mode: "insensitive",
      },
    },
    select: {
      id: true,
      id_client: true,
      password: true,
      m_role: {
        select: { name: true },
      },
    },
  });
  if (userdb) {
    const doesPasswordMatch = bcrypt.compareSync(
      form.password,
      userdb?.password,
    );
    if (userdb.password === " ") {
      navigate(`/auth/change-password/${form.username}`);
    } else {
      if (doesPasswordMatch === true) {
        let ses = await db.m_session.findFirst({
          where: { id_user: userdb.id },
        });
        if (!ses) {
          ses = await db.m_session.create({
            data: {
              id_user: userdb.id,
              id_client: userdb.id_client,
            },
          });
        }
        localStorage.setItem("mlsid", ses.session_id);
        const user = await reloadSession();

        const role = userdb.m_role.name;
        form.username = "";
        form.password = "";
        form._saving = false;
        form.render();

        if (role === "ppds") {
          navigate("/ppds/home");
          form.render();
        } else if (role === "staff") {
          navigate("/staff-pengajar/home");
          form.render();
        }

        auditTrail({
          type: "action",
          activity: `Login`,
        });
        form.render();
      } else {
        alert("Invalid Password");
      }
    }
  } else {
    alert("Invalid Username");
  }
}

