load.value:
async () => {
  let data = await db.t_notif.findMany({
    include: {
      m_user: {
        select: {
          display_name: true,
        },
      },
    },
    where: {
      id_user: user.id,
      id_client: user.id_client,
    },
  });
  return data.map((item) => {
    return {
      name: item.m_user.display_name,
      timestamp: moment(new Date(item.date)).fromNow(),
      message: item.message,
      status: "read",
    };
  });
}

