async (f) => {
  const filter = {};
  if (f.id_ppds) {
    filter["id"] = parseInt(f.id_ppds);
  }
  if (f.id_semester) {
    filter["id_semester"] = parseInt(f.id_semester);
  }

  const data = await db.m_user.findMany({
    orderBy: { m_semester: { name: "asc" } },
    where:
      filter.id !== undefined
        ? {
            id: filter.id,
            is_show: true,
            id_client: Number(user.id_client),
          }
        : filter.id_semester !== undefined
        ? {
            id_semester: filter.id_semester,
            is_show: true,
            id_client: Number(user.id_client),
          }
        : {
            m_role: {
              name: "ppds",
            },
            is_show: true,
            id_client: Number(user.id_client),
          },
    include: {
      m_semester: {
        include: {
          m_stage: true,
        },
      },
      m_stase: true,
    },
  });

  let result = data.reduce((r, a) => {
    r[a.m_semester.name] = r[a.m_semester.name] || [];
    r[a.m_semester.name].push(a);
    return r;
  }, Object.create(null));

  return Object.keys(result).map((item, idx) => {
    return {
      semester: item,
      data: result[item],
    };
  });
}

