async () => {
  let data = await db.m_semester.findMany({
    where: {
      id_client: Number(user.id_client),
    },
    orderBy: {
      name: "asc",
    },
  });
  let datanya = data.map((item) => {
    return {
      label: item.name,
      value: item.id,
    };
  });
  (datanya as any).unshift({
    label: "Filter Semester",
    value: "null",
  });
  return datanya;
}

