async () => {
  if (confirm("Are you sure to update this data ?")) {
    let datas = {
      type: local.activity_name,
      has_assignment: local.assignment,
      has_learning_materials: local.lm,
      updated_date: new Date(),
      updated_by: user.id,
    };
    let save = await db.m_activity_type.update({
      where: {
        id: Number(new URLSearchParams(window.location.search).get("id")),
      },
      data: datas,
    });
    local.assignment = false;
    local.activity_name = null;
    local.lm = false;
    local.render();
    navigate("/institution/data/activity-type");
  }
}

