sample.value:
async () => {
  const w = window as any as {
    idClient: number;
    idSemester: number;
    homeActions: any[];
    hideActions: any[];
  };

  if (w.idClient !== user.id_client) {
    w.idClient = user.id_client;
    w.homeActions = undefined;
    w.hideActions = undefined;
  }

  if (w.idSemester !== user.id_smt) {
    w.idSemester = user.id_smt;
    w.homeActions = undefined;
    w.hideActions = undefined;
  }

  if (!w.hideActions) {
    w.hideActions = await db.m_user_action.findMany({
      where: {
        id_user: Number(user.id),
        id_client: Number(user.id_client),
      },
    });
  }

  if (!w.homeActions) {
    w.homeActions = await db.m_action.findMany({
      select: { id: true, name: true, m_action_type: true },
      orderBy: { m_action_type: { id: "asc" } },
      where: {
        id_client: Number(user.id_client),
        m_action_semester: {
          some: {
            id_semester: user.id_smt,
          },
        },
      },
    });
  }

  let actions = (w.homeActions || []).filter((item) => {
    let found = w.hideActions?.find((o) => o.id_action_hidden === item.id);
    if (found) {
      return false;
    }
    return true;
  });

  return { actions };
}
