load.value:
async (filter) => {
  console.log(filter);
  const data = await db.t_logbook.findMany({
    where: {
      id_action: 3,
      id_user: user.id,
    },
    select: {
      id: true,
      date: true,
      verified: true,
      t_logbook_participants: {
        where: {
          id_logbook_role: 2,
        },
        select: {
          m_user: {
            select: {
              display_name: true,
            },
          },
        },
      },
    },
  });
  return data.map((item) => {
    const parti = item.t_logbook_participants;
    return {
      id: item.id,
      title: moment(item.date).format("DD MM YYYY - hh:mm"),
      status: item.verified ? "Verified" : "-",
      doctor: parti.length > 0 ? parti[0].m_user.display_name : "...",
    };
  });
}

