action_function.value:
async () => {
  let active_check = false;
  if (
    new Date() >= new Date(local.startDate) &&
    new Date() <= new Date(local.endDate)
  ) {
    active_check = true;
  }
  let save = await db.m_academic_year.create({
    data: {
      name: local.name,
      is_active: active_check,
      start_time: local.startDate,
      end_time: local.endDate,
      created_by: user.id,
      created_date: new Date(),
      id_institution: 2,
    },
  });
  if (save) {
    if (confirm("Data Saved, Input again?")) {
      local.name = null;
      local.startDate = null;
      local.endDate = null;
      local.render();
    } else {
      local.name = null;
      local.startDate = null;
      local.endDate = null;
      local.render();
      navigate("/institution/data/academic-years");
    }
  }
}

