action_function.value:
async () => {
  let data = await db.m_user.findFirst({
    where: {
      email: local.email,
    },
  });
  if (data) {
    const doesPasswordMatch = bcrypt.compareSync(local.old_pass, data.password);
    if (doesPasswordMatch === true) {
      local.access = true;
      local.user_id = data.id;
      local.render();
    } else {
      alert("Invalid Password, Try Again");
    }
  } else {
    alert("Invalid Email, Try Again");
  }
}

