action_function.value:
async () => {
  if (
    local.new_pass !== null &&
    local.confirm_pass !== null &&
    local.confirm_pass === local.new_pass
  ) {
    const salt = bcrypt.genSaltSync(10);
    const hashedPassword = bcrypt.hashSync(local.confirm_pass, salt);
    let update = await db.m_user.update({
      where: {
        id: Number(local.user_id),
        email: local.email,
      },
      data: {
        password: hashedPassword,
      },
    });
    if (update) {
      alert("Your password has changed");
      navigate('/auth/login')
      local.old_pass = null;
      local.email = null;
      local.confirm_pass = null;
      local.new_pass = null;
      local.user_id = null;
      local.access = false;
      local.render();
    } else {
      alert("An error occuring");
    }
  } else {
    alert("Check your new password or confirmation password");
  }
}
