async (f) => {
  const filter = {};

  if (f.id_pengajar) {
    filter["t_logbook_participants"] = {
      some: { id_user: parseInt(f.id_pengajar), id_logbook_role: 2 },
    };
  }
  if (f.date) {
    filter["date"] = {
      gt: new Date(moment(f.date).startOf("day").toString()),
      lt: new Date(moment(f.date).add(1, "day").startOf("day").toString()),
    };
  }
  if (f.id_hospital) filter["id_hospital"] = parseInt(f.id_hospital);
  if (f.notes) {
    filter["notes"] = {
      contains: f.notes,
      mode: "insensitive",
    };
  }

  const data = await db.t_logbook.findMany({
    orderBy: { date: "asc" },
    where: {
      id_action: 1,
      id_user: user.id,
      ...filter,
    },
    select: {
      id: true,
      date: true,
      verified: true,
      t_logbook_participants: {
        where: {
          id_logbook_role: 2,
        },
        select: {
          m_user: {
            select: {
              display_name: true,
            },
          },
        },
      },
    },
  });
  return data.map((item) => {
    const parti = item.t_logbook_participants;
    return {
      id: item.id,
      title: moment(item.date).format("DD MMMM YYYY - hh:mm"),
      status: item.verified ? "Verified" : "-",
      doctor: parti.length > 0 ? parti[0].m_user.display_name : "...",
      // kskslalalallxxxx
    };
  });
}

