async (f) => {
  const filter = {};
  const data = await db.t_logbook.findMany({
    orderBy: { date: "desc" },
    where:
      user.role === "staff"
        ? {
            id_action: Number(params.id_action),
            id_category: Number(params.id),
            t_logbook_status: {
              some: {
                id_user: Number(user.id),
              },
            },
            ...filter,
          }
        : {
            id_action: Number(params.id_action),
            id_user: Number(user.id),
            id_category: Number(params.id),
            ...filter,
          },
    include: {
      t_logbook_status: {
        include: {
          m_action_role: { select: { identifier: true } },
          m_user: { select: { id: true, display_name: true } },
        },
      },
      m_action_category: true,
      m_hospital: {
        select: { name: true },
      },
      m_user: {
        select: {
          display_name: true,
        },
      },
    },
  });

  return data.map((item) => {
    const roles = { peserta: item.m_user.display_name };
    for (const o of item.t_logbook_status) {
      roles[o.m_action_role.identifier] = o.m_user.display_name;
    }

    let hospital = item.m_hospital?.name;

    return {
      id: item.id,
      date: moment(item.date).format("DD MMMM YYYY - HH:mm"),
      status: item.verified ? "Verified" : "-",
      title: item.title,
      operation_code: item.operation_code,
      ...roles,
      hospital,
      location: item.location,
    };
  });
}

