action_function.value:
async () => {
  const salt = bcrypt.genSaltSync(10);
  const hashedPassword = bcrypt.hashSync(local.name, salt);
  if (confirm("Are you sure to update this data ?")) {
    let datas = {
      display_name: local.name,
      username: local.name,
      password: hashedPassword,
      id_role: 1,
      is_deleted: false,
      created_date: new Date(),
      created_by: user.id,
      phone: local.phone,
      address: local.address,
      date_of_birth: local.dateBirth,
      email: local.email,
    };
    let save = await db.m_user.update({
      where: {
        id: Number(new URLSearchParams(window.location.search).get("id")),
      },
      data: datas,
    });
    local.name = null;
    local.phone = null;
    local.address = null;
    local.dateBirth = null;
    local.email = null;
    local.render();
    navigate("/institution/data/ppds");
  }
}

