async (f) => {
  const list = await db.t_notif.findMany({
    where: {
      id_user: Number(user.id),
    },
    include: {
      m_user: true,
    },
    orderBy: {
      date: "desc",
    },
  });

  let list_notif = list.map((e) => {
    return {
      id: e.id,
      title:
        e.type === "verify"
          ? "Logbook Menunggu Verifikasi"
          : e.type === "verified"
          ? "Logbook Diverifikasi"
          : e.type === "revised"
          ? "Verifikasi Logbook Dibatalkan"
          : "Pemberitahuan",
      message: e.message,
      date: moment(e.date).fromNow(),
      read: e.read,
      type: e.type,
      url: e.url,
    };
  });
  return list_notif;
}

