action_function.value:
async () => {
  let active_check = false;
  if (
    new Date() >= new Date(local.startDate) &&
    new Date() <= new Date(local.endDate)
  ) {
    active_check = true;
  }
  if (confirm("Are you sure to update this data?")) {
    await db.m_academic_year.update({
      where: {
        id: Number(new URLSearchParams(window.location.search).get("id")),
      },
      data: {
        name: local.name,
        start_time: local.startDate,
        end_time: local.endDate,
        updated_by: user.id,
        updated_date: new Date(),
        is_active: active_check,
      },
    });
    navigate("/institution/data/academic-years");
  }
}

