load.value:
async (filter) => {
  const data = await db.t_logbook.findMany({
    where: {
      id_action: 4,
      id_user: user.id,
      ...filter,
    },
    select: {
      id: true,
      date: true,
      verified: true,
      operation_code: true,
      m_operation_type: {
        select: {
          operation_type: true,
        }
      },
      t_logbook_participants: {
        where: {
          id_logbook_role:2,
        },
        select: {
          m_user: {
            select: {
              display_name: true,
            },
          },
        },
      },
    },
  });
  return data.map((item) => {
    const parti = item.t_logbook_participants;
    return {
      id: item.id,
      code: item.operation_code,
      status: item.verified,
      doctor: parti.length > 0 ? parti[0].m_user.display_name : "...",
      role: item.m_operation_type.operation_type,
      date: moment(item.date).format("DD MM YYYY - hh:mm"),
    };
  });
}

