async () => {
  const datas = await db.m_action.findMany({
    where: {
      id_client: user.id_client,
      show_on_menu: true,
      m_action_semester: {
        some: {
          id_semester: user.id_smt,
        },
      },
    },
    include: {
      m_action_type: {
        select: {
          name: true,
        },
      },
      m_action_semester: true,
    },
    orderBy: {
      m_action_type: {
        id: 'asc'
      }
    }
  });

  let result = datas.reduce(function (r, a) {
    r[a.m_action_type.name] = r[a.m_action_type.name] || [];
    r[a.m_action_type.name].push(a);
    return r;
  }, Object.create(null));

  return { result };
}
