sample.value:
async () => {
  if (isEditor && (window as any).homeicon) {
    return { actions: (window as any).homeicon };
  }

  let hide_menu = await db.m_user_action.findMany({
    where: {
      id_user: Number(user.id),
    },
  });
  const no_filter_actions = await db.m_action.findMany({
    select: {
      id: true,
      name: true,
      m_action_type: true,
    },
    where: {
      id_client: user.id_client,
      show_on_menu: true
    },
    orderBy: { m_action_type: { id: "asc" } },
  });
  let actions = no_filter_actions.filter((item) => {
    let hide = hide_menu?.find((o) => o.id_action_hidden === item.id);
    if (hide === undefined) {
      return item;
    }
  });

  if (isEditor && actions.length > 0) (window as any).homeicon = actions;

  return { actions };
}

