load.value:
async () => {
  let id = parseInt(params.id);
  if (isEditor) id = 5;
  const dbuser = await db.m_user.findFirst({
    where: { id },
    select: {
      id: true,
      display_name: true,
      code: true,
      address: true,
      date_of_birth: true,
      id_semester: true,
      picture: true,
    },
  });

  const count = await db.t_logbook.groupBy({
    by: ["id_action"],
    where: {
      id_user: id,
    },
    _count: {
      id_action: true,
    },
  });
  const raw = await db.m_action.findMany({
    where: {
      id: {
        in: count.map((e) => e.id_action),
      },
    },
    select: {
      id: true,
      name: true,
    },
  });

  const log = {};
  for (const item of raw) {
    log[item.id] = { id: item.id, label: item.name };
  }
  for (const item of count) {
    log[item.id_action].quantity = item._count.id_action;
  }

  const user = {
    id: dbuser.id,
    Nama: dbuser.display_name,
    NIM: dbuser.code,
    Alamat: dbuser.address,
    "Tanggal Lahir": moment(dbuser.date_of_birth).format("DD MMM YYYY"),
    id_semester: dbuser.id_semester,
    pic: dbuser.picture,
  };
  const umap = [];
  for (const [k, v] of Object.entries(user)) {
    if (k !== "pic" && !k.includes("id")) {
      umap.push({ label: k, value: v });
    }
  }

  const res = { user, umap, log: Object.values(log) };
  return res;
}

