async (f) => {
  const filter = {};
  // if (f.id_action) filter["id_action"] = Number(f.id_action);

  const data = await db.t_logbook_status.findMany({
    where: {
      status: "pending",
      id_user: user.id,
    },
    select: {
      id_logbook: true,
    },
    orderBy: {
      date_time: "desc",
    },
  });

  const list = await db.t_logbook.findMany({
    where: f.id_action
      ? {
          id: {
            in: data.map((e) => e.id_logbook),
          },
          id_action: {
            equals: Number(f.id_action),
          },
        }
      : {
          id: {
            in: data.map((e) => e.id_logbook),
          },
        },
    select: {
      id: true,
      id_action: true,
      m_action: {
        select: {
          name: true,
        },
      },
      m_user: {
        select: {
          display_name: true,
        },
      },
      date: true,
    },
    orderBy: {
      date: "desc",
    },
  });

  let list_notif = list.map((e) => {
    return {
      id: e.id,
      id_action: e.id_action,
      title: e.m_action.name,
      message: e.m_user.display_name,
      date: moment(e.date).format("DD MMM YYYY HH:mm"),
    };
  });
  return list_notif;
}

